  //header

let observer = new IntersectionObserver((entries, observer)=>{
    entries.forEach(
      entry =>{
        if(entry.isIntersecting){
            $('.header').removeClass('_active');
      }else{
        $('.header').addClass('_active');
      }
    }
  )
}, { threshold: 1})

document.querySelectorAll('.header').forEach(
  p => {observer.observe(p)}
);


